package com.haima.sage.bigdata.script

import java.util.Collections
import com.haima.sage.bigdata.analyzer.streaming.functions._
import com.haima.sage.bigdata.etl.common.model.{AsyncAnalyzer, RedisSource, RichMap}
import org.apache.flink.streaming.api.functions.async.ResultFuture

case class $name(override val conf: AsyncAnalyzer[RedisSource]) extends RedisAsyncFunction {

  /**
   * 生成缓存key的逻辑
   *
   * @param data
   * @return
   */
  override def loadDataKey(data: RichMap): String =
    data.getOrElse("cloud_id", "").toString


  /**
   * 获取到缓存后的处理逻辑
   *
   * @param input
   * @param cache
   * @return
   */
  override def process(input: RichMap, cache: List[Option[RichMap]]): RichMap = {
        cache.head match {
          case Some(add_value) =>
            input ++ add_value
            add_value.get("value") match {
                case Some(v:String) =>
                  val add_value_new = mapper.readValue[Map[String,Any]](v)
                  input ++ (add_value_new -  ("log_time"))
                case _ =>
                input
            }
          case _ =>
            input
        }
  }


  /**
    * 获取到缓存失败的处理逻辑
    * @param input
    * @param resultFuture
    * @return
    */
  override def onFail(input: RichMap, resultFuture: ResultFuture[RichMap])(throwable: Throwable): Unit = {
    resultFuture.complete(Collections.singleton(input))
  }

  /**
   * 生成数据key的逻辑
   *
   * @param data
   * @return
   */
  override def dataKey(data: RichMap): List[String] = {
    data.get("cloud_id").map( t=>"core_"+t.toString).toList
  }
}

