import org.apache.commons.lang3.time.FastDateFormat

//TODO 其他类定义
case class $name() extends RichMapMapping {
  //TODO 变量定义
  val defaultFormat = FastDateFormat.getInstance("yyyy_MM_dd_HH_mm_ss_S")
  val defaultFormat1 = FastDateFormat.getInstance("yyyy-MM-dd HH:mm:ss.S")
  val defaultFormat2 = FastDateFormat.getInstance("yyyy-MM-dd")
  val defaultFormat3 = FastDateFormat.getInstance("yyyyMMdd")

  //时间和key处理方法
  def timeProcess(event: RichMap): RichMap = {
    var result = RichMap()
    result ++= event

    //数据生成时间
    result.get("data_generation_time") match {
      case Some(x) =>
        result ++= Map("es_index_time" -> defaultFormat3.format(x.toString.toLong),
          "hdfs_time" -> defaultFormat2.format(x.toString.toLong),
          "log_time" -> defaultFormat1.format(x.toString.toLong))
      case None =>
        result.get("log_time") match {
          case Some(x) =>
            val log_time_long = defaultFormat.parse(x.toString).getTime
            result ++= Map("es_index_time" -> defaultFormat3.format(log_time_long),
              "hdfs_time" -> defaultFormat2.format(log_time_long),
              "log_time" -> defaultFormat1.format(log_time_long),
              "data_generation_time" -> log_time_long)
        }
    }
    
    result
  }

  override def process(event: RichMap): RichMap = {
    //TODO 业务逻辑
    timeProcess(event)
  }
}
